styles.css:
.widget {
    padding-left: 3rem;
    padding-right: 3rem;
    padding-top: 3rem;
    padding-bottom: 3rem;
    color: black;
    border-radius: 25px;
    width: 400px;
height: 280px;
    opacity: 1.0;
    background-color: rgba(240, 248, 255, 0.5);
    backdrop-filter:blur(5px);
}

@media (max-width: 1280px) {
    .widget {
        padding-left: 3rem;
    padding-right: 3rem;
    padding-top: 3rem;
    padding-bottom: 3rem;
    color: black;
    border-radius: 25px;
    width: 100%;
height: 280px;
    opacity: 1.0;
    background-color: rgba(240, 248, 255, 0.5);
    backdrop-filter:blur(5px);
margin: 0 auto;
    }
.grid {
width: 100%;
margin: 0 auto;
}
.container {
    padding-left: 0rem;
    padding-right: 0rem;
}
}

.post {
    opacity: 1.0;
    background-color: rgba(240, 248, 255, 0.5);
    backdrop-filter:blur(5px);
border-radius: 25px;
    padding-left: 5%;
    padding-right: 5%;
padding-top: 2rem;
    padding-bottom: 2rem;
    margin-top: 2rem;
    margin-bottom: 2rem;
}


.container {
    padding-left: 3rem;
    padding-right: 3rem;
    padding-top: 3rem;
    padding-bottom: 3rem;
    color: black;
    border-radius: 25px;
    opacity: 1.0;
    background-color: rgba(240, 248, 255, 0.5);
    backdrop-filter:blur(5px);
width: 80%;
margin: 0 auto;
float: right;
}

.cat {
    padding-top: 3rem;
    padding-bottom: 3rem;
    color: black;
    border-radius: 25px;
    width: 400px;
height: 280px;
    opacity: 1.0;
    background-color: rgba(240, 248, 255, 0.5);
    backdrop-filter:blur(5px);
}


.cat-h {
padding-left: 3rem;
}

.cat-item {
padding-left: 1rem;
height: 2rem;
list-style-type: none;
}

.cat-item a {
text-decoration: none;
}

.widget-home {
    padding-left: 3rem;
    padding-top: 1rem;
padding-bottom: 1rem;
    color: black;
    border-radius: 25px;
    width: 400px;
height: 70px;
    opacity: 1.0;
    background-color: rgba(240, 248, 255, 0.5);
    backdrop-filter:blur(5px);
}

.widget-title {
text-decoration: none;
}
