styles.css:
.widget {
    padding-left: 3rem;
    padding-right: 3rem;
    padding-top: 3rem;
    padding-bottom: 3rem;
    width: 100%;
    height: 280px;
}

.grid {
padding: 2rem;}
@media (max-width: 1280px) {
    .grid {
        width: 100%;
        margin: 0 auto;
    }
}

.post {
    opacity: 1.0;
    background-color: rgba(240, 248, 255, 0.5);
    backdrop-filter:blur(5px);
    border-radius: 25px;
    padding-left: 5%;
    padding-right: 5%;
    padding-top: 2rem;
    padding-bottom: 2rem;
    margin-top: 2rem;
    margin-bottom: 2rem;
}

.container {
    padding-left: 3rem;
    padding-right: 3rem;
    padding-top: 3rem;
    padding-bottom: 3rem;
    width: 100%;
    margin: 0 auto;
}

.cat {
    padding-top: 3rem;
    padding-bottom: 3rem;
    width: 100%;
    height: 280px;
}

.cat-h {
    padding-left: 3rem;
}

.cat-item {
    padding-left: 1rem;
    height: 2rem;
    list-style-type: none;
}

.cat-item a {
    text-decoration: none;
}

.widget-home {
    padding-left: 3rem;
    padding-right: 3rem;
    padding-top: 0.5rem;
    padding-bottom: 0.5rem;
    width: 100%;
    height: 70px;
display: flex;
justify-content: center;
align-items: center;
font-size: 24px;
margin: 0 auto;
}

.widget-title {
    text-decoration: none;
}

.widget-style {
    opacity: 1.0;
    background-color: rgba(240, 248, 255, 0.5);
    backdrop-filter:blur(5px);
    border-radius: 25px;
}

.columns-limit {
min-width: 280px; }

.not-fund {
padding: 3rem;}
