styles.css:
.widget {
    padding-left: 3rem;
    padding-right: 3rem;
    padding-top: 3rem;
    padding-bottom: 3rem;
    color: black;
    border-radius: 25px;
    width: 400px;
height: 280px;
    opacity: 1.0;
    background-color: rgba(240, 248, 255, 0.5);
    backdrop-filter:blur(5px);
}

.post {
    opacity: 1.0;
    background-color: rgba(240, 248, 255, 0.5);
    backdrop-filter:blur(5px);
border-radius: 25px;
    padding-left: 5%;
    padding-right: 5%;
padding-top: 2rem;
    padding-bottom: 2rem;
    margin-top: 2rem;
    margin-bottom: 2rem;
}

.grid {
margin-left: 2rem;
}

.container {
    padding-left: 3rem;
    padding-right: 3rem;
    padding-top: 3rem;
    padding-bottom: 3rem;
    color: black;
    border-radius: 25px;
    opacity: 1.0;
    background-color: rgba(240, 248, 255, 0.5);
    backdrop-filter:blur(5px);
width: 80%;
margin: 0 auto;
}
